use pest::iterators::Pair; // Removed unused Pairs
use std::collections::HashMap;
use crate::ast::{Expression, MapKey};
use super::Rule; // Import Rule from the parent module (mod.rs)
use super::common::{build_literal, build_symbol, build_keyword, build_map_key}; // Import from sibling
use super::special_forms::{build_let_expr, build_if_expr, build_do_expr, build_fn_expr, build_def_expr, build_defn_expr}; // Import from sibling

pub(super) fn build_expression(pair: Pair<Rule>) -> Expression {
    // Drill down through silent rules if necessary.
    // The primary rule we expect here is 'expression', which contains the actual concrete type.
    let actual_pair = match pair.as_rule() {
        Rule::expression => { // Only expect 'expression' at this top level of the function
             pair.into_inner().next().unwrap_or_else(|| panic!("Expected inner rule for expression: {:?}", pair.as_str()))
        }
        // If called recursively with an already concrete rule (like from map or vector builders)
        _ => pair,
    };

    match actual_pair.as_rule() {
        Rule::literal => Expression::Literal(build_literal(actual_pair)),
// ...existing code...
        Rule::defn_expr => Expression::Defn(build_defn_expr(actual_pair.into_inner())),

        // Added function_call rule
        Rule::function_call => {
            let mut inner = actual_pair.into_inner();
            let func = build_expression(inner.next().expect("Function call needs a function expression"));
            let args = inner.map(build_expression).collect();
            Expression::FunctionCall { // Assuming AST has FunctionCall variant
                function: Box::new(func),
                arguments: args,
            }
        }

        rule => unimplemented!("build_expression not implemented for rule: {:?} - {}", rule, actual_pair.as_str()),
    }
}
// ...existing code...
pub(super) fn build_map(pair: Pair<Rule>) -> HashMap<MapKey, Expression> {
    assert_eq!(pair.as_rule(), Rule::map);
    let mut map = HashMap::new();
    // map = { "{" ~ map_entry* ~ "}" }
    // map_entry = { map_key ~ expression }
    let mut map_content = pair.into_inner(); // Should contain map_entry pairs

    while let Some(entry_pair) = map_content.next() {
        // Skip whitespace/comments if the grammar allows them inside map
        if entry_pair.as_rule() == Rule::WHITESPACE || entry_pair.as_rule() == Rule::COMMENT { continue; }

        assert_eq!(entry_pair.as_rule(), Rule::map_entry, "Expected map_entry inside map");
        let mut entry_inner = entry_pair.into_inner();
        let key_pair = entry_inner.next().expect("Map entry missing key");
        // Skip potential whitespace/comment between key and value
        let value_pair = entry_inner.find(|p| p.as_rule() != Rule::WHITESPACE && p.as_rule() != Rule::COMMENT)
                                  .expect("Map entry missing value");


        let key = build_map_key(key_pair); // Use the specific map key builder
        let value = build_expression(value_pair);
        map.insert(key, value);
    }
    map
}

