    // But the rule name inside vector might be param_def or binding_pattern
    // Let's assume the grammar is: fn_params = { "[" ~ param_def* ~ ("&" ~ symbol)? ~ "]" }
    // param_def = { binding_pattern ~ (":" ~ type_expr)? }
    assert!(params_pair.as_rule() == Rule::vector || params_pair.as_rule() == Rule::fn_param_list, "Expected vector or fn_param_list for fn params");

    let mut params: Vec<ParamDef> = Vec::new();
    let mut variadic_param: Option<Symbol> = None;
    let mut params_inner = params_pair.into_inner().peekable();

    while let Some(param_item_peek) = params_inner.peek() {
        // Skip whitespace/comments
        if param_item_peek.as_rule() == Rule::COMMENT || param_item_peek.as_rule() == Rule::WHITESPACE {
            params_inner.next(); // Consume whitespace/comment
            continue;
        }

        // Check for variadic '&'
        if param_item_peek.as_str() == "&" {
