            // Consume potential whitespace after &
            while let Some(peeked) = params_inner.peek() {
                 if peeked.as_rule() == Rule::COMMENT || peeked.as_rule() == Rule::WHITESPACE {
                     params_inner.next(); // Consume
                 } else {
