use pest::iterators::{Pair, Pairs};
use crate::ast::{LetExpr, LetBinding, IfExpr, DoExpr, FnExpr, DefExpr, DefnExpr, ParamDef, Symbol, TypeExpr}; // Added Symbol, TypeExpr
use super::Rule; // Import Rule from the parent module (mod.rs)
use super::common::{build_symbol, build_pattern}; // Removed build_param_def (handled inline or pattern used directly)
use super::types::build_type_expr; // Import from sibling
use super::expressions::build_expression; // Import from sibling

// --- Specific Expression Builders ---

pub(super) fn build_let_expr(mut pairs: Pairs<Rule>) -> LetExpr {
    // let_expr = { "(" ~ let_kw ~ binding_vector ~ expression+ ~ ")" }
    // pairs = binding_vector, expression, expression, ...
    let bindings_pair = pairs.next().expect("Let expression requires bindings");
    // Assuming the grammar rule for bindings is binding_vector
    assert_eq!(bindings_pair.as_rule(), Rule::binding_vector, "Expected binding_vector for let bindings");

    let mut bindings = Vec::new();
    // binding_vector = { "[" ~ (binding)* ~ "]" }
    // binding = { pattern ~ type_expr? ~ expression }
    let mut binding_pairs = bindings_pair.into_inner().peekable(); // Pairs inside the vector [...]

    while let Some(pattern_pair) = binding_pairs.next() {
        // Skip whitespace/comments
        if pattern_pair.as_rule() == Rule::COMMENT || pattern_pair.as_rule() == Rule::WHITESPACE { continue; }
        assert_eq!(pattern_pair.as_rule(), Rule::binding_pattern, "Expected binding_pattern in let");
        let pattern = build_pattern(pattern_pair);

        let mut type_annotation: Option<TypeExpr> = None;

        // Peek for optional type annotation, consuming whitespace
        while let Some(peeked) = binding_pairs.peek() {
            if peeked.as_rule() == Rule::WHITESPACE || peeked.as_rule() == Rule::COMMENT {
                binding_pairs.next(); // Consume whitespace/comment
            } else if peeked.as_rule() == Rule::type_expr {
                // Found type annotation, consume it
                type_annotation = Some(build_type_expr(binding_pairs.next().unwrap()));
                break; // Stop peeking for type
            } else {
                break; // Next token is not type or whitespace, must be value
            }
        }

        // Consume potential whitespace/comments before value
        while let Some(peeked) = binding_pairs.peek() {
             if peeked.as_rule() == Rule::COMMENT || peeked.as_rule() == Rule::WHITESPACE {
                 binding_pairs.next(); // Consume
             } else {
                 break;
             }
        }

        // The next non-comment/whitespace must be the value expression
        let value_pair = binding_pairs.next().expect("Let binding requires a value expression");
        let value = Box::new(build_expression(value_pair));

        bindings.push(LetBinding { pattern, type_annotation, value });
    }

    let body = pairs.map(build_expression).collect();
    LetExpr { bindings, body }
}


pub(super) fn build_if_expr(mut pairs: Pairs<Rule>) -> IfExpr {
    // if_expr = { "(" ~ if_kw ~ expression ~ expression ~ expression? ~ ")" }
    // pairs = condition, then_branch, else_branch?
    let condition = Box::new(build_expression(pairs.next().unwrap()));
    let then_branch = Box::new(build_expression(pairs.next().unwrap()));
    let else_branch = pairs.next().map(|p| Box::new(build_expression(p))); // Optional else

    IfExpr { condition, then_branch, else_branch }
}

pub(super) fn build_do_expr(pairs: Pairs<Rule>) -> DoExpr {
     // do_expr = { "(" ~ do_kw ~ expression* ~ ")" }
     // pairs = expression, expression, ...
    let expressions = pairs.map(build_expression).collect();
    DoExpr { expressions }
}

pub(super) fn build_fn_expr(mut pairs: Pairs<Rule>) -> FnExpr {
    // fn_expr = { "(" ~ fn_kw ~ fn_params ~ fn_body ~ ")" }
    // fn_params = { vector } // Assuming vector for params: [p1 p2] or [& rest] or [p1 & r]
    // fn_body = { type_expr? ~ expression+ }
    // pairs = fn_params, fn_body_elements...

    // 1. Parameters
    let params_pair = pairs.next().expect("fn requires parameters vector");
    // Expect Rule::vector for parameters based on common syntax
    assert_eq!(params_pair.as_rule(), Rule::vector, "Expected vector for fn params");

    let mut params: Vec<ParamDef> = Vec::new();
    let mut variadic_param = None; // Changed name to match AST
    let mut params_inner = params_pair.into_inner().peekable();

    while let Some(param_item_peek) = params_inner.peek() {
        // Skip whitespace/comments
        if param_item_peek.as_rule() == Rule::COMMENT || param_item_peek.as_rule() == Rule::WHITESPACE {
            params_inner.next(); // Consume whitespace/comment
            continue;
        }

        // Check for variadic '&'
        if param_item_peek.as_str() == "&" {
            params_inner.next(); // Consume '&'
            // Consume potential whitespace after &
            while let Some(peeked) = params_inner.peek() {
                 if peeked.as_rule() == Rule::COMMENT || peeked.as_rule() == Rule::WHITESPACE {
                     params_inner.next(); // Consume
                 } else {
                     break;
                 }
            }
            let rest_symbol_pair = params_inner.next().expect("& must be followed by a symbol");
            assert_eq!(rest_symbol_pair.as_rule(), Rule::symbol, "& must be followed by a symbol");
            variadic_param = Some(build_symbol(rest_symbol_pair));
            break; // No more regular params after &
        }

        // Process regular parameter (pattern + optional type)
        let pattern_pair = params_inner.next().unwrap(); // Consume the pattern part
        assert!(pattern_pair.as_rule() == Rule::binding_pattern || pattern_pair.as_rule() == Rule::param_def, "Expected binding_pattern or param_def in fn params");

        // If it's param_def, it might contain the type internally
        let (pattern, mut type_annotation) = if pattern_pair.as_rule() == Rule::param_def {
            let mut inner_param_def = pattern_pair.into_inner();
            let pattern = build_pattern(inner_param_def.next().unwrap());
            let type_ann = inner_param_def.find(|p| p.as_rule() == Rule::type_expr).map(build_type_expr);
            (pattern, type_ann)
        } else {
            // It's just a binding_pattern, check *next* item for type
            let pattern = build_pattern(pattern_pair);
            let mut type_ann: Option<TypeExpr> = None;
            // Peek for optional type annotation, consuming whitespace
            while let Some(peeked) = params_inner.peek() {
                if peeked.as_rule() == Rule::WHITESPACE || peeked.as_rule() == Rule::COMMENT {
                    params_inner.next(); // Consume whitespace/comment
                } else if peeked.as_rule() == Rule::type_expr {
                    // Found type annotation, consume it
                    type_ann = Some(build_type_expr(params_inner.next().unwrap()));
                    break; // Stop peeking for type
                } else {
                    break; // Next token is not type or whitespace
                }
            }
            (pattern, type_ann)
        };

        params.push(ParamDef { pattern, type_annotation });
    }

    // 2. Body (Optional Return Type + Expressions)
    let mut body_pairs = pairs.peekable();
    let mut return_type = None;

    // Consume potential whitespace/comments before body
    while let Some(peeked) = body_pairs.peek() {
         if peeked.as_rule() == Rule::COMMENT || peeked.as_rule() == Rule::WHITESPACE {
             body_pairs.next(); // Consume
         } else {
             break;
         }
    }

    // Check if the first body element is a type expression (return type)
    if let Some(first_body_pair) = body_pairs.peek() {
        if first_body_pair.as_rule() == Rule::type_expr {
            return_type = Some(build_type_expr(body_pairs.next().unwrap()));
            // Consume potential whitespace/comments after return type
            while let Some(peeked) = body_pairs.peek() {
                 if peeked.as_rule() == Rule::COMMENT || peeked.as_rule() == Rule::WHITESPACE {
                     body_pairs.next(); // Consume
                 } else {
                     break;
                 }
            }
        }
    }

    // The rest are body expressions
    let body = body_pairs.map(build_expression).collect();

    FnExpr { params, variadic_param, return_type, body } // Updated field name
}


pub(super) fn build_def_expr(mut pairs: Pairs<Rule>) -> DefExpr {
    // def_expr = { "(" ~ def_kw ~ symbol ~ type_expr? ~ expression ~ ")" }
    // pairs = symbol, type_expr?, expression
    let symbol_pair = pairs.next().unwrap(); // Changed name
    assert_eq!(symbol_pair.as_rule(), Rule::symbol);
    let symbol = build_symbol(symbol_pair); // Changed name

    let mut type_annotation = None;
    let mut value_pair = pairs.next().unwrap(); // Assume it's value initially

    // Check if the first element after name is a type_expr
    if value_pair.as_rule() == Rule::type_expr {
        type_annotation = Some(build_type_expr(value_pair));
        // Consume potential whitespace/comments after type
        while let Some(peeked) = pairs.peek() {
             if peeked.as_rule() == Rule::COMMENT || peeked.as_rule() == Rule::WHITESPACE {
                 pairs.next(); // Consume
             } else {
                 break;
             }
        }
        value_pair = pairs.next().expect("def requires a value expression after type annotation");
    }

    let value = Box::new(build_expression(value_pair));

    DefExpr { symbol, type_annotation, value } // Updated field name
}

