use pest::Parser;
use pest::iterators::Pair;

// Re-export Rule from the generated parser
// pub use pest::RuleType; // Make RuleType usable - Commented out, seems unused directly

// Declare submodules
pub mod common;
pub mod expressions;
pub mod special_forms;
pub mod types;
pub mod utils;

// Import AST types needed at this level
use crate::ast::TopLevel; // Only TopLevel needed here

// Import builder functions from submodules
use expressions::build_expression;

// Define the parser struct using the grammar file
#[derive(pest_derive::Parser)]
#[grammar = "rtfs.pest"] // Path relative to src/
struct RTFSParser;

// --- Main Parsing Function ---

pub fn parse(input: &str) -> Result<Vec<TopLevel>, pest::error::Error<Rule>> {
    let pairs = RTFSParser::parse(Rule::program, input)?;
    // Program contains SOI ~ (task_definition | module_definition | expression)* ~ EOI
    // The `pairs` variable is an iterator over the content matched by Rule::program.
    // We need its single inner item (which should be the sequence inside program)
    // then iterate over *that* sequence's inner items.
    let program_content = pairs.peek().expect("Parse should have yielded one program rule");
    let top_level_pairs = program_content.into_inner()
                               .filter(|p| p.as_rule() != Rule::WHITESPACE && p.as_rule() != Rule::COMMENT && p.as_rule() != Rule::EOI);
                               // Keep EOI filter as it's implicitly added by Pest

    Ok(top_level_pairs.map(build_ast).collect())
}

// --- AST Builder Functions ---

fn build_ast(pair: Pair<Rule>) -> TopLevel {
    // This function now receives pairs like expression, task_definition, module_definition directly.
    // No need to assert Rule::top_level or drill down further.
    match pair.as_rule() {
        Rule::expression => TopLevel::Expression(build_expression(pair)),
        Rule::task_definition => unimplemented!("build_ast not implemented for rule: {:?}", pair.as_rule()),
        Rule::module_definition => unimplemented!("build_ast not implemented for rule: {:?}", pair.as_rule()),
        // Add other top-level rules from the grammar as needed
        // Rule::import_definition => ...
        // Rule::type_definition => ... (If added to grammar)
        // Rule::resource_definition => ... (If added to grammar)

        // Handle unexpected rules at this level
        rule => unimplemented!("build_ast encountered unexpected top-level rule: {:?}, content: '{}'", rule, pair.as_str()),
    }
}

