use pest::iterators::Pair;
use crate::ast::{Keyword, Literal, MapKey, Pattern, Symbol}; // Removed ParamDef, TypeExpr
use super::Rule; // Import Rule from the parent module (mod.rs)
use super::utils::unescape; // Import unescape from sibling module
use super::types::build_type_expr; // Import from sibling module
use super::expressions::build_expression; // Import from sibling module (needed for pattern/param building)

// --- Helper Builders ---

pub(super) fn build_literal(pair: Pair<Rule>) -> Literal {
     let inner_pair = pair.into_inner().next().unwrap(); // Get the actual literal type rule
    match inner_pair.as_rule() {
        Rule::integer => Literal::Integer(inner_pair.as_str().parse().expect("Invalid integer literal")),
        Rule::float => Literal::Float(inner_pair.as_str().parse().expect("Invalid float literal")),
        Rule::string => {
            let raw_str = inner_pair.as_str();
            // Remove quotes and unescape
            let content = &raw_str[1..raw_str.len() - 1];
            Literal::String(unescape(content).expect("Invalid escape sequence in string"))
        }
        Rule::boolean => Literal::Boolean(inner_pair.as_str().parse().expect("Invalid boolean literal")),
        Rule::nil => Literal::Nil,
        rule => unimplemented!("build_literal not implemented for rule: {:?}", rule),
    }
}

pub(super) fn build_symbol(pair: Pair<Rule>) -> Symbol {
    assert_eq!(pair.as_rule(), Rule::symbol);
    Symbol(pair.as_str().to_string())
}

pub(super) fn build_keyword(pair: Pair<Rule>) -> Keyword {
    assert_eq!(pair.as_rule(), Rule::keyword);
    // Remove the leading ':'
    Keyword(pair.as_str()[1..].to_string())
}

// Placeholder - needs implementation based on grammar for map keys
pub(super) fn build_map_key(pair: Pair<Rule>) -> MapKey {
    // map_key = { keyword | string | integer } // Assuming based on AST
     match pair.as_rule() {
         Rule::keyword => MapKey::Keyword(build_keyword(pair)),
         Rule::string => {
             let raw_str = pair.as_str();
             let content = &raw_str[1..raw_str.len() - 1];
             MapKey::String(unescape(content).expect("Invalid escape sequence in map key string"))
         }
         Rule::integer => MapKey::Integer(pair.as_str().parse().expect("Invalid integer map key")),
         rule => unimplemented!("build_map_key not implemented for rule: {:?}", rule),
     }
}

// Placeholder - needs implementation based on grammar for patterns
pub(super) fn build_pattern(pair: Pair<Rule>) -> Pattern {
    // pattern = { symbol | map_pattern | vector_pattern | literal | wildcard } // Assuming based on AST
    match pair.as_rule() {
        Rule::symbol => Pattern::Symbol(build_symbol(pair)),
        Rule::literal => Pattern::Literal(build_literal(pair)),
        // TODO: Implement map_pattern, vector_pattern, wildcard based on grammar rules
        // Rule::map_pattern => Pattern::MapPattern(...),
        // Rule::vector_pattern => Pattern::VectorPattern(...),
        // Rule::wildcard => Pattern::Wildcard,
        rule => unimplemented!("build_pattern not implemented for rule: {:?} - {}", rule, pair.as_str()),
    }
}

// Builds a parameter definition (pattern + optional type)
pub(super) fn build_param_def(pair: Pair<Rule>) -> ParamDef {
    // Assuming param_def = { pattern ~ type_expr? } based on usage in fn/defn
    // This might need adjustment if the grammar rule is different.
    // If param_def is just a symbol, we treat it as a pattern directly.
    match pair.as_rule() {
        Rule::symbol => {
             // Simple case: just a symbol name
             ParamDef {
                 pattern: Pattern::Symbol(build_symbol(pair)),
                 type_annotation: None,
             }
        }
        // TODO: Add cases if param_def is a more complex rule in pest
        // e.g., Rule::param_def => { let mut inner = pair.into_inner(); ... }
        _ => {
             // Fallback: Assume the pair *is* the pattern
             // This handles cases where the grammar might pass a literal or other pattern directly
             // We need to check if a type annotation follows *outside* this function
             // (as done in build_fn_expr/build_defn_expr)
             // This function might need refactoring depending on the exact grammar rules for fn_params/defn_params
             ParamDef {
                 pattern: build_pattern(pair), // Try building a pattern directly
                 type_annotation: None, // Type is handled separately in fn/defn builders
             }
        }
    }

    // Original logic from fn/defn builders suggests type is handled *after* the param item.
    // Let's stick to the simpler version for now, assuming build_param_def only handles the pattern part.
    // The type annotation logic remains in build_fn_expr/build_defn_expr.

