pub(super) fn build_with_resource_expr(mut pairs: Pairs<Rule>) -> WithResourceExpr {
    let binding_pair = pairs
        .next()
        .expect("with-resource requires a binding vector");
    // Assuming grammar uses vector `[...]` for the binding part
    assert_eq!(
        binding_pair.as_rule(),
        Rule::vector,
        "Expected vector for with-resource binding"
    );
    let mut binding_inner = binding_pair.into_inner();

    let resource_symbol =
        build_symbol(next_significant(&mut binding_inner).expect("with-resource needs symbol"));
    let resource_type =
        build_type_expr(next_significant(&mut binding_inner).expect("with-resource needs type"));
    let resource_init = Box::new(build_expression(
        next_significant(&mut binding_inner).expect("with-resource needs init expression"),
    ));

    let body = pairs.map(build_expression).collect();

    WithResourceExpr {
        resource_symbol,
        resource_type,
        resource_init,
        body,
    }
}
