        Rule::keyword => CatchPattern::Keyword(build_keyword(actual_pair)),
        Rule::symbol => CatchPattern::Symbol(build_symbol(actual_pair)),
        rule => unimplemented!("build_catch_pattern not implemented for rule: {:?}", rule),
    }
}
