// In d:\workspaces\mandubian\lang_ai\rtfs_compiler\src\parser\special_forms.rs
// This function is called from expressions.rs when "log-step" is identified
// as the first element of a list_expr.
// It receives the *arguments* to "log-step", which are expected to be:
// 1. The keyword :id
// 2. A string literal for the ID
// 3. An expression to be logged
pub(super) fn build_log_step_expr(mut pairs: Pairs<Rule>) -> LogStepExpr {
    println!("[special_forms.rs/build_log_step_expr] Received pairs: {:?}", pairs.clone().collect::<Vec<_>>());

    // 1. Parse :id keyword
    let id_key_pair = pairs.next().expect("log-step requires :id keyword as first argument");
    println!("[special_forms.rs/build_log_step_expr] id_key_pair: {:?}", id_key_pair);
    // This pair comes from parsing an expression, so it should be Rule::keyword_expr or similar,
    // or if rtfs.pest has keyword as a basic token, then Rule::keyword.
    // The debug output showed: Pair { rule: keyword, ... }
    assert_eq!(id_key_pair.as_rule(), Rule::keyword, "Expected first argument to log-step to be Rule::keyword (for :id)");
    assert_eq!(id_key_pair.as_str(), ":id", "Expected keyword to be ':id'");

    // 2. Parse string ID
    let id_value_pair = pairs.next().expect("log-step :id needs string value as second argument");
    println!("[special_forms.rs/build_log_step_expr] id_value_pair: {:?}", id_value_pair);
    // This pair comes from parsing an expression, so it should be Rule::string_expr,
    // or if rtfs.pest has string as a basic token, then Rule::string.
    // The debug output showed: Pair { rule: string, ... }
    assert_eq!(id_value_pair.as_rule(), Rule::string, "Expected second argument to log-step to be Rule::string (for the ID)");
    let id_str_raw = id_value_pair.as_str();
    let id_content = &id_str_raw[1..id_str_raw.len() - 1]; // Remove quotes
    let id = unescape(id_content).expect("Invalid escape sequence in log-step id string");
    println!("[special_forms.rs/build_log_step_expr] Parsed id: {}", id);

    // 3. Parse expression
    let expression_argument_pair = pairs.next().expect("log-step requires an expression as third argument");
    println!("[special_forms.rs/build_log_step_expr] expression_argument_pair: {:?}", expression_argument_pair);
    // This expression_argument_pair is already a 'Rule::expression' itself, based on the debug output.
    // Pair { rule: expression, start: 30, end: 45, inner: [Pair { rule: fn_call_expr, ... }] }
    // So, we pass this directly to build_expression.
    let expression = Box::new(build_expression(expression_argument_pair.clone())); // Clone for debug
    println!("[special_forms.rs/build_log_step_expr] Parsed expression for log-step: {:?}", expression);

    if pairs.next().is_some() {
        panic!("log-step received too many arguments");
    }

    let result = LogStepExpr { id, expression };
    println!("[special_forms.rs/build_log_step_expr] Constructed LogStepExpr: {:?}", result);
    result
}

